    func insertWord(word: String, colorIndex: Int, pos: Int) {
        choosedData.insert(word, at: pos)
        choosedColor.insert(colorIndex, at: pos)
        len4Choosed += 1;
        wordCollectionView.reloadData()
        dataHistory.push(choosedData)
        colorHistory.push(choosedColor)
        len4Undo += 1
    }
    
    func addWord(words: [String], colorIndex: Int) {
        for word in words {
            choosedData.append(word)
            choosedColor.append(colorIndex)
        }
        len4Choosed += words.count
        wordCollectionView.reloadData()
        dataHistory.push(choosedData)
        colorHistory.push(choosedColor)
        len4Undo += 1
    }
    
    func deleteWord(pos:Int) {
        choosedData.remove(at: pos)
        choosedColor.remove(at: pos)
        len4Choosed -= 1;
        wordCollectionView.reloadData()
        dataHistory.push(choosedData)
        colorHistory.push(choosedColor)
        len4Undo += 1
    }
    
