            self.lastStep.frame = CGRect(x: origin.minX + 74,y: origin.minY,width: origin.size.width,height: origin.size.height)
            origin = self.backtoTop.frame
            self.backtoTop.frame =  CGRect(x: origin.minX + 74,y: origin.minY,width: origin.size.width,height: origin.size.height)
            self.showMention = false
        }
    }
    @IBAction func taptoFirst(_ sender: Any) {
        toFirst()
    }
    func toFirst(){
        if(self.showMention){
            var origin = self.lastStep.frame
            self.lastStep.frame = CGRect(x: origin.minX + 74,y: origin.minY,width: origin.size.width,height: origin.size.height)
            origin = self.backtoTop.frame
            self.backtoTop.frame =  CGRect(x: origin.minX + 74,y: origin.minY,width: origin.size.width,height: origin.size.height)
            self.showMention = false
        }
        step = 0
        history = [0]
        chooseWordsRow = 0
        backtoTop.alpha = 0.0
        lastStep.alpha = 0.0
        needHelp.alpha = 0.0
        let animation = CABasicAnimation(keyPath: "opacity")
        animation.fromValue = 1
        animation.toValue = 0
        animation.duration = 0.5
        animation.delegate = self
        animation.isRemovedOnCompletion = false
        animation.fillMode = CAMediaTimingFillMode.forwards
        mention.layer.add(animation, forKey: "addLayerAnimationOpacity")
    }
    @IBAction func backTop(_ sender: Any) {
        if(step==0){
            
            return
        }
        step -= 1
        if(step==0){
            backtoTop.alpha = 0.0
            lastStep.alpha = 0.0
            needHelp.alpha = 0.0
        }
        history.removeLast()
        chooseWordsRow = history.removeLast()
        history.append(chooseWordsRow)
        let animation = CABasicAnimation(keyPath: "opacity")
        animation.fromValue = 1
        animation.toValue = 0
        animation.duration = 0.5
        animation.delegate = self
        animation.isRemovedOnCompletion = false
        animation.fillMode = CAMediaTimingFillMode.forwards
        mention.layer.add(animation, forKey: "addLayerAnimationOpacity")
    }

    func superUICollectionViewCell(of: UIButton) -> UICollectionViewCell? {
           for view in sequence(first: of.superview, next: { $0?.superview }) {
               if let cell = view as? UICollectionViewCell {
                   return cell
               }
           }
           return nil
       }
