    // 拖拽手势处理
    @objc func handlePanGesture(_ gesture: UIPanGestureRecognizer) {
        switch(gesture.state) {
            
        case .began:
            guard let selectedIndexPath = self.wordCollectionView.indexPathForItem(at: gesture.location(in: self.wordCollectionView)) else {
                break
            }
            // 开始交互
            wordCollectionView.beginInteractiveMovementForItem(at: selectedIndexPath)
        case .changed:
            // 更新位置
            wordCollectionView.updateInteractiveMovementTargetPosition(gesture.location(in: gesture.view!))
        case .ended:
            // 结束交互
            wordCollectionView.endInteractiveMovement()
        default:
            // 默认取消交互
            wordCollectionView.cancelInteractiveMovement()
        }
    }
    
    // 长按手势处理
    @objc func handleLongPressGesture(_ gesture: UILongPressGestureRecognizer) {
        switch(gesture.state) {
            
        case .began:
            guard let selectedIndexPath = self.wordCollectionView.indexPathForItem(at: gesture.location(in: self.wordCollectionView)) else {
                break
            }
            // 开始交互
            let cell = wordCollectionView.cellForItem(at: selectedIndexPath)
            wordCenter = cell!.center
            wordCenter.x = wordCenter.x - wordCollectionView.contentOffset.x
            tempWord = choosedData[selectedIndexPath.item]
