//        case .changed:
//            // 更新位置
        case .ended:
            // 结束交互
            performSegue(withIdentifier: "toWordPopOver", sender: self)
        default: break
            // 默认取消交互
            
//            wordCollectionView.cancelInteractiveMovement()
        }
    }
