extension ViewController: CAAnimationDelegate ,UICollectionViewDataSource, UICollectionViewDelegate{
    func collectionView(_ collectionView: UICollectionView, layout collectionViewLayout: UICollectionViewLayout, insetForSectionAt section: Int) -> UIEdgeInsets {
            var elements_count = 0
            if collectionView == descriptionCollectionView{
                elements_count = descriptionData.count
            }else if collectionView == keyWordsCollectionView{
                elements_count = keywordsData.count
            }
            else{
                elements_count = chooseWordsData.count
            }
            //Where elements_count is the count of all your items in that
            //Collection view...
            let cellCount = CGFloat(elements_count)
        print(cellCount)
            //If the cell count is zero, there is no point in calculating anything.
            if cellCount > 0 {
                let flowLayout = collectionViewLayout as! UICollectionViewFlowLayout
                let cellWidth = flowLayout.itemSize.width + flowLayout.minimumInteritemSpacing

                //20.00 was just extra spacing I wanted to add to my cell.
                let totalCellWidth = cellWidth*cellCount + 20.00 * (cellCount-1)
                let contentWidth = collectionView.frame.size.width - collectionView.contentInset.left - collectionView.contentInset.right

                if (totalCellWidth < contentWidth) {
                    //If the number of cells that exists take up less room than the
                    //collection view width... then there is an actual point to centering them.

                    //Calculate the right amount of padding to center the cells.
                    let padding = (contentWidth - totalCellWidth) / 2.0
                    return UIEdgeInsets(top: 0, left: padding, bottom: 0, right: padding)
                } else {
                    //Pretty much if the number of cells that exist take up
                    //more room than the actual collectionView width, there is no
                    // point in trying to center them. So we leave the default behavior.
                    return UIEdgeInsets(top: 0, left: 40, bottom: 0, right: 40)
                }
            }

            return UIEdgeInsets.zero
        }
    func collectionView(_ collectionView: UICollectionView, numberOfItemsInSection section: Int) -> Int {
        if collectionView == wordCollectionView{
            return choosedData.count
        }
        else if collectionView == descriptionCollectionView{
            return descriptionData.count
        }else if collectionView == keyWordsCollectionView{
            return keywordsData.count
        }
        else if collectionView == chooseWordCollectionView{
            return chooseWordsData.count
        }
        else{
            return chooseData.count
        }
    }
    
