    func collectionView(_ collectionView: UICollectionView, cellForItemAt indexPath: IndexPath) -> UICollectionViewCell {
        if collectionView == wordCollectionView{
            guard let cell = collectionView.dequeueReusableCell(withReuseIdentifier: "wordCell", for: indexPath) as? WordCollectionViewCell else {
                fatalError()
            }
            cell.imageView.image = UIImage(named: choosedData[indexPath.row])
            cell.label.text=choosedData[indexPath.row]
            cell.label.backgroundColor = labelColor[choosedColor[indexPath.row]]
            cell.backgroundColor = backgroudColor[choosedColor[indexPath.row]]
            
            return cell
        }
        else if collectionView == chooseWordCollectionView{
            guard let cell = collectionView.dequeueReusableCell(withReuseIdentifier: "chooseWords", for: indexPath) as? WordsChoiceCollectionViewCell else {
                fatalError()
            }
            cell.image.image = UIImage(named: chooseWordsData[indexPath.row])
            cell.label.setTitle(chooseWordsData[indexPath.row], for: .normal)
            return cell
        }else if collectionView == keyWordsCollectionView{
            guard let cell = collectionView.dequeueReusableCell(withReuseIdentifier: "keyWords", for: indexPath) as? KeyWordsCollectionViewCell else {
                fatalError()
            }
            cell.content.image = UIImage(named: keywordsData[indexPath.row])
            cell.keywords.text = keywordsData[indexPath.row]
            return cell
        }
        else if collectionView == descriptionCollectionView{
            guard let cell = collectionView.dequeueReusableCell(withReuseIdentifier: "description", for: indexPath) as? DescriptionCollectionViewCell else {
                fatalError()
            }

            cell.image.image = UIImage(named: descriptionData[indexPath.row])
            cell.label.text = descriptionData[indexPath.row]
            return cell
        }
        else{
            guard let cell = collectionView.dequeueReusableCell(withReuseIdentifier: "chooseCell", for: indexPath) as? ChooseCollectionViewCell else {
                fatalError()
            }
            cell.imageView.frame.size = cell.frame.size
            cell.imageView.image = UIImage(named: chooseData[indexPath.row])
            
            return cell
        }
        
    }
    
