    func animationDidStop(_ anim: CAAnimation, finished flag: Bool) {
        if flag {
            if(someDict.keys.contains(chooseWord)){
                mention.text = mentionData[step][chooseWordsRow]
            }else{
                mention.text = "嗨！我可以根据您的选择，帮助您查找「目标词」。\n\n您会从以下哪方面来描述这一「目标词」呢？"
            }
            let animation = CABasicAnimation(keyPath: "opacity")
            animation.fromValue = 0
            animation.toValue = 1
            animation.duration = 0.5
            animation.isRemovedOnCompletion = false
            animation.fillMode = CAMediaTimingFillMode.forwards
            mention.layer.add(animation, forKey: "addLayerAnimationOpacity")
            
            descriptionData = totalData[step][chooseWordsRow]
            print(descriptionData)
            descriptionCollectionView.reloadData()
            
        } else {
            print("动画被停止")
        }
    }
    
    /// 更新我们自己的数据源
    func collectionView(_ collectionView: UICollectionView, moveItemAt sourceIndexPath: IndexPath, to destinationIndexPath: IndexPath) {
        let word = choosedData.remove(at: sourceIndexPath.item)
        choosedData.insert(word, at: destinationIndexPath.item)
        let color = choosedColor.remove(at: sourceIndexPath.item)
        choosedColor.insert(color, at: destinationIndexPath.item)
        dataHistory.push(choosedData)
        colorHistory.push(choosedColor)
        len4Undo += 1
    }
